package collections;

import java.util.ArrayList;

public class DifferentWaysOfconstructingAnArrayList {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arrayList[] = { 1, 2, 3, 4, 5, 6 };
		String List1[] = { "java", ".net", "ui" };
		ArrayList a1 = new ArrayList();
		a1.add(10);
		a1.add(20);
		a1.add(30);
		System.out.println("arrayList lenght is:" + arrayList.length);
		System.out.println("String lenght is: " + List1.length);
		System.out.println("a1 size is: " + a1.size());

	}
}
